(399, '_transient_wc_addons_section_product', 'O:8:"stdClass":1:{s:8:"products";a:24:{i:0;O:8:"stdClass":5:{s:5:"title";s:15:"Product Add-Ons";s:5:"image";s:0:"";s:7:"excerpt";s:104:"Allow your customers to customize products by adding input boxes, dropdowns, file uploads or checkboxes.";s:4:"link";s:110:"https://www.woothemes.com/products/product-add-ons/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;49.00";}i:1;O:8:"stdClass":5:{s:5:"title";s:15:"Dynamic Pricing";s:5:"image";s:0:"";s:7:"excerpt";s:48:"Bulk discounts, role-based pricing and much more";s:4:"link";s:110:"https://www.woothemes.com/products/dynamic-pricing/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:11:"&#36;129.00";}i:2;O:8:"stdClass":5:{s:5:"title";s:15:"Product Bundles";s:5:"image";s:0:"";s:7:"excerpt";s:149:"Create and offer highly configurable product bundles, kits and assemblies that consist of simple and variable items - both physical and downloadable.";s:4:"link";s:110:"https://www.woothemes.com/products/product-bundles/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;49.00";}i:3;O:8:"stdClass":5:{s:5:"title";s:21:"Checkout Field Editor";s:5:"image";s:0:"";s:7:"excerpt";s:128:"The checkout field editor provides you with an interface to add, edit and remove fields shown on your WooCommerce checkout page.";s:4:"link";s:128:"https://www.woothemes.com/products/woocommerce-checkout-field-editor/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;49.00";}i:4;O:8:"stdClass":5:{s:5:"title";s:29:"Gravity Forms Product Add-ons";s:5:"image";s:0:"";s:7:"excerpt";s:39:"Powerful product add-ons, Gravity style";s:4:"link";s:116:"https://www.woothemes.com/products/gravity-forms-add-ons/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;99.00";}i:5;O:8:"stdClass":5:{s:5:"title";s:10:"TradeGecko";s:5:"image";s:71:"https://www.woothemes.com/wp-content/uploads/2015/09/tradegecko_new.jpg";s:7:"excerpt";s:216:"TradeGecko is a cloud-based inventory management platform for retailers and wholesalers that want effortless commerce. Enhance your business with advanced inventory and order management connected to your WooCommerce.";s:4:"link";s:117:"https://www.woothemes.com/products/woocommerce-tradegecko/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:9:"&#36;0.00";}i:6;O:8:"stdClass":5:{s:5:"title";s:22:"Groups for WooCommerce";s:5:"image";s:0:"";s:7:"excerpt";s:84:"Sell memberships using the free ''Groups'' plugin, Groups integration and WooCommerce.";s:4:"link";s:113:"https://www.woothemes.com/products/groups-woocommerce/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;79.00";}i:7;O:8:"stdClass":5:{s:5:"title";s:18:"Min/Max Quantities";s:5:"image";s:0:"";s:7:"excerpt";s:82:"Specify minimum and maximum allowed product quantities for orders to be completed.";s:4:"link";s:112:"https://www.woothemes.com/products/minmax-quantities/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;29.00";}i:8;O:8:"stdClass":5:{s:5:"title";s:29:"Variation Swatches and Photos";s:5:"image";s:0:"";s:7:"excerpt";s:73:"Show color and image swatches instead of dropdowns for variable products.";s:4:"link";s:124:"https://www.woothemes.com/products/variation-swatches-and-photos/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;99.00";}i:9;O:8:"stdClass":5:{s:5:"title";s:15:"Name your price";s:5:"image";s:0:"";s:7:"excerpt";s:90:"Allow customers to define the product price. Also useful for accepting user-set donations.";s:4:"link";s:110:"https://www.woothemes.com/products/name-your-price/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;49.00";}i:10;O:8:"stdClass":5:{s:5:"title";s:18:"Composite Products";s:5:"image";s:0:"";s:7:"excerpt";s:104:"Create complex, configurable product kits and let your customers build their own, personalized versions.";s:4:"link";s:113:"https://www.woothemes.com/products/composite-products/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;79.00";}i:11;O:8:"stdClass":5:{s:5:"title";s:6:"Brands";s:5:"image";s:0:"";s:7:"excerpt";s:84:"Create, assign and list brands for products, and allow customers to filter by brand.";s:4:"link";s:101:"https://www.woothemes.com/products/brands/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;29.00";}i:12;O:8:"stdClass":5:{s:5:"title";s:15:"Product Vendors";s:5:"image";s:0:"";s:7:"excerpt";s:47:"Turn your store into a multi-vendor marketplace";s:4:"link";s:110:"https://www.woothemes.com/products/product-vendors/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;79.00";}i:13;O:8:"stdClass":5:{s:5:"title";s:22:"Advanced Notifications";s:5:"image";s:0:"";s:7:"excerpt";s:96:"Easily setup "new order" and stock email notifications for multiple recipients of your choosing.";s:4:"link";s:117:"https://www.woothemes.com/products/advanced-notifications/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;29.00";}i:14;O:8:"stdClass":5:{s:5:"title";s:24:"WooCommerce Social Login";s:5:"image";s:90:"https://www.woothemes.com/wp-content/uploads/2014/08/wc_social-login_feat-img@2x.jpg?w=639";s:7:"excerpt";s:62:"Enable Social Login for Seamless Checkout and Account Creation";s:4:"link";s:119:"https://www.woothemes.com/products/woocommerce-social-login/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;79.00";}i:15;O:8:"stdClass":5:{s:5:"title";s:23:"WooCommerce Memberships";s:5:"image";s:0:"";s:7:"excerpt";s:44:"Sell memberships via your WooCommerce store.";s:4:"link";s:118:"https://www.woothemes.com/products/woocommerce-memberships/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:11:"&#36;149.00";}i:16;O:8:"stdClass":5:{s:5:"title";s:28:"WooCommerce Checkout Add-Ons";s:5:"image";s:0:"";s:7:"excerpt";s:59:"Easily Offer Free or Paid Add-ons and Services at Checkout.";s:4:"link";s:123:"https://www.woothemes.com/products/woocommerce-checkout-add-ons/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;49.00";}i:17;O:8:"stdClass":5:{s:5:"title";s:23:"Newsletter Subscription";s:5:"image";s:0:"";s:7:"excerpt";s:127:"Allow customers to subscribe to your MailChimp or CampaignMonitor mailing list(s) via a widget or by opting in during checkout.";s:4:"link";s:118:"https://www.woothemes.com/products/newsletter-subscription/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;49.00";}i:18;O:8:"stdClass":5:{s:5:"title";s:13:"EU VAT Number";s:5:"image";s:0:"";s:7:"excerpt";s:85:"Collect VAT numbers at checkout and remove the VAT charge for eligible EU businesses.";s:4:"link";s:108:"https://www.woothemes.com/products/eu-vat-number/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;29.00";}i:19;O:8:"stdClass":5:{s:5:"title";s:22:"WooCommerce Pre-Orders";s:5:"image";s:0:"";s:7:"excerpt";s:60:"Allow customers to order products before they are available.";s:4:"link";s:117:"https://www.woothemes.com/products/woocommerce-pre-orders/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:11:"&#36;129.00";}i:20;O:8:"stdClass":5:{s:5:"title";s:29:"WooCommerce One Page Checkout";s:5:"image";s:0:"";s:7:"excerpt";s:159:"Make it super fast for customers to purchase with your store. Create special pages where customers can choose products, checkout &amp; pay all on the one page!";s:4:"link";s:124:"https://www.woothemes.com/products/woocommerce-one-page-checkout/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;79.00";}i:21;O:8:"stdClass":5:{s:5:"title";s:20:"WooCommerce Waitlist";s:5:"image";s:0:"";s:7:"excerpt";s:117:"With WooCommerce Waitlist customers can regi